// export default Next;

class Next extends Component{
  
    render(props){
        // const newName_=this.props.namee; 
        // const newAge_=this.props.age; 
        const {products}=this.props;
        const allProducts=products.map((item)=>{
            if(item.id>2){
              return(
                <div key={item.id}>
                    <h2>{item.name}</h2>
                    <h5>{item.age}</h5>
                </div>
              )
        }});
        return(
            <div>
           { allProducts}
        </div> 
        )
    }
}
export default Next;
