import React, { Component } from "react";

import "./App.css"
import HomePage from "./HomePages/HomePage";
import Next from "./Next";


class App extends Component{//the same name of component
  state={
    cars:[
        {
            name:"rana",
            age:23
        },
        {
            name:"ahmed",
            age:25
        },
        {
            name:"Nour",
            age:22
        }
    ]
}
 changeData=(newName)=>{
  this.setState({
    cars:[
      {
          name:newName,
          age:23
      },
      {
          name:"ahmed",
          age:25
      },
      {
          name:"Nour",
          age:22
      }
  ]
  })
 }
  render(){
  return(//we should use one div
  <div>
    <HomePage/>
    <Next namee={this.state.cars[0].name} age={this.state.cars[0].age}/>
    <button className="btn btn-primary" onClick={this.changeData.bind(this,)}>change</button>
  </div>
  )
}
}
export default App;
